function getQueryObj(url) {
  let arr = url.split("?")[1].split("#")[0].split("&");
  const resObj = {};
  arr.forEach((item) => {
    let [key, value = ""] = item.split("=");
    resObj[key] = value;
  });
  return resObj;
}

export { getQueryObj };
